mapper.update:
UPDATE BOARD SET
	VIEW_CNT = #{viewCnt} + 1
WHERE BOARD_TYPE = #{boardType}
AND BOARD_NUM = #{boardNum}

mapper.select:
SELECT CODE_NAME ,CODE_ID ,BOARD_TYPE ,BOARD_NUM ,BOARD_TITLE ,BOARD_COMMENT ,TOTAL_CNT ,VIEW_CNT FROM ( SELECT CC.CODE_NAME ,CC.CODE_ID ,BO.BOARD_TYPE ,BO.BOARD_NUM ,BO.BOARD_TITLE ,BO.BOARD_COMMENT ,ROW_NUMBER() OVER(ORDER BY BOARD_NUM DESC) AS NUMROW ,COUNT(*) OVER() AS TOTAL_CNT ,BO.VIEW_CNT FROM BOARD BO, COM_CODE CC WHERE BO.BOARD_TYPE = CC.CODE_ID
AND CC.CODE_ID IN (#{codeId})
) WHERE NUMROW BETWEEN (#{pageNo}-1)*10 + 1 AND (#{pageNo})* 10

mapper.delete:
DELETE FROM BOARD
WHERE BOARD_TYPE = #{boardType}
AND BOARD_NUM = #{boardNum}

mapper.update:
UPDATE BOARD SET 
	BOARD_TITLE = #{boardTitle} 
  , BOARD_COMMENT = #{boardComment}
WHERE BOARD_TYPE = #{boardType}
AND BOARD_NUM = #{boardNum}
